import React, { useState } from 'react';
import apidata1 from './api';

function Howitworks() {
    const [api,setApi]=useState(apidata1)
    return (
        <>
        <center>
        
            <div className='howitworks shadow-lg pb-2'>
                <div className='text-center py-3 hiws'>
                    <h3>How It Works</h3>
                </div>
                       <div className='display d-flex flex-row'>
               {api.map((cure,index)=>{
                   const {id,icon,text,para}=cure
                   return(

                    
                    <div className='px-5 d-flex flex-column py-3 htw'>
                        <div className='pb-3'>

                             

                             <span className='iconrounder w-100 text-c'><i class={icon}></i></span>
                             
                        </div>
                        <div className='text-center'>

                        <h4>{text}</h4>
                        </div>
                        <div className='w-100 hiwp'>

                        <p>{para}</p>
                        </div>
                    </div>
                    

                       
               );
               })}
                    
            </div>
            </div>
            </center>
            
            
            
        </>
    );
}

export default Howitworks;
